#main-header, .et-fixed-header { position: fixed !important; top: 0 !important; width: 100% !important; z-index: 99999 !important; }

/* Logo responsive forzado */
.et_pb_image_wrap img { max-width: 110px !important; height: auto !important; width: auto !important; }
@media (min-width: 480px) { .et_pb_image_wrap img { max-width: 130px !important; } }
@media (min-width: 768px) { .et_pb_image_wrap img { max-width: 150px !important; } }

/* Menú desktop */
@media (min-width: 981px) {
  .et-menu { display: flex !important; align-items: center !important; gap: 8px !important; }
  .et-menu > li { padding: 0 12px !important; }
  .et-menu > li > a { 
    padding: 8px 0 !important; 
    font-weight: 600 !important;
    color: #1b1f24 !important;
    transition: color 0.3s ease !important;
    position: relative !important;
  }
  .et-menu > li > a::after {
    content: '' !important;
    position: absolute !important;
    bottom: 0 !important;
    left: 50% !important;
    width: 0 !important;
    height: 2px !important;
    background: #0f6df2 !important;
    transition: all 0.3s ease !important;
    transform: translateX(-50%) !important;
  }
  .et-menu > li > a:hover::after,
  .et-menu > li.current-menu-item > a::after { width: 100% !important; }
  .et-menu > li > a:hover { color: #0f6df2 !important; }

  /* Dropdown */
  .et-menu .sub-menu {
    border-radius: 12px !important;
    box-shadow: 0 10px 30px rgba(0,0,0,0.15) !important;
    border: 1px solid #e5e9ef !important;
    padding: 8px !important;
    margin-top: 10px !important;
    background: #ffffff !important;
    min-width: 220px !important;
  }
  .et-menu .sub-menu li a {
    padding: 10px 16px !important;
    border-radius: 8px !important;
    transition: all 0.25s ease !important;
    color: #1b1f24 !important;
  }
  .et-menu .sub-menu li a:hover {
    background: rgba(15,109,242,0.08) !important;
    color: #0f6df2 !important;
    padding-left: 20px !important;
  }
}

/* Menú móvil */
@media (max-width: 980px) {
  .et_mobile_menu_bar { padding: 0 !important; }
  .mobile_menu_bar {
    display: flex !important;
    align-items: center !important;
    justify-content: center !important;
    padding: 8px !important;
    cursor: pointer !important;
  }
  .mobile_menu_bar::before {
    font-size: 28px !important;
    color: #1b1f24 !important;
  }

  .et_mobile_menu {
    border-radius: 0 0 16px 16px !important;
    box-shadow: 0 8px 24px rgba(0,0,0,0.12) !important;
    background: #ffffff !important;
    padding: 0 !important;
  }

  .et_mobile_menu ul { padding: 0 !important; margin: 0 !important; }

  .et_mobile_menu li {
    border-bottom: 1px solid #e5e9ef !important;
    margin: 0 !important;
  }

  .et_mobile_menu li:last-child { border-bottom: none !important; }

  .et_mobile_menu li a {
    padding: 14px 20px !important;
    font-size: 15px !important;
    font-weight: 600 !important;
    color: #1b1f24 !important;
    display: block !important;
    transition: all 0.25s ease !important;
  }

  .et_mobile_menu li a:hover {
    background: rgba(15,109,242,0.06) !important;
    color: #0f6df2 !important;
    padding-left: 28px !important;
  }
}

/* Botón CTA */
.et_pb_button { 
  display: inline-flex !important; 
  align-items: center !important; 
  gap: 6px !important;
  white-space: nowrap !important;
}

/* Header sticky animation */
.et_pb_section_sticky {
  animation: slideDown 0.3s ease-out !important;
}

@keyframes slideDown {
  from { transform: translateY(-100%) !important; }
  to { transform: translateY(0) !important; }
}

/* Performance */
.et_pb_section, .et_pb_column, .et_pb_module {
  transform: translateZ(0) !important;
  backface-visibility: hidden !important;
}
